DEPENDENCY_INSTALL_MODE="${DEPENDENCY_INSTALL_MODE:-pip}"; cd /home/container || exit 1; if [[ -d .git ]] && [[ "{{AUTO_UPDATE}}" == "1" ]]; then git pull; fi; if [[ "${DEPENDENCY_INSTALL_MODE}" == "uv" ]]; then export UV_PROJECT_ENVIRONMENT="/home/container/.local/uv"; if [[ -f /home/container/pyproject.toml ]]; then uv sync; exec "${UV_PROJECT_ENVIRONMENT}/bin/python" "/home/container/{{PY_FILE}}"; else exec /usr/local/bin/python "/home/container/{{PY_FILE}}"; fi; else if [[ -n "{{PY_PACKAGES}}" ]]; then uv pip install -U --prefix .local {{PY_PACKAGES}}; fi; if [[ -f "/home/container/${REQUIREMENTS_FILE}" ]]; then uv pip install -U --prefix .local -r "${REQUIREMENTS_FILE}"; fi; PYVER=$(/usr/local/bin/python -c 'import sys; print(f"{sys.version_info.major}.{sys.version_info.minor}")'); export PATH="/home/container/.local/bin:${PATH}"; export PYTHONPATH="/home/container/.local/lib/python${PYVER}/site-packages:${PYTHONPATH}"; exec /usr/local/bin/python "/home/container/{{PY_FILE}}"; fi